import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import Axios from 'axios';
import serverUrl from '../common';

const userUrl = 'api/user';

export const getUserByUsernameAsync = createAsyncThunk(
    'api/user/getUserByUsernameAsync',
    async ({ username }) => {
        const response = await Axios.get(
            `${serverUrl}${userUrl}/get-user-by-username?username=${username}`
        );
        const tasks = response.data;
        return { tasks };
    }
);

export const updateUserAsync = createAsyncThunk('', async ({ userReq }) => {
    const formData = new FormData();
    formData.append('name', userReq.name);
    formData.append('alias', userReq.alias);
    formData.append('gender', userReq.gender);
    formData.append('slug', userReq.slug);
    formData.append('content', userReq.content);
    formData.append('quote', userReq.quote);
    formData.append('date', userReq.date);
    formData.append('location', userReq.date);
    const response = await Axios.put(
        `${serverUrl}${userUrl}/update-user`,
        formData
    );
    const tasks = response.data;
    return { tasks };
});

export const userSlice = createSlice({
    name: 'user',
    initialState: {
        user: null,
    },
    reducers: {},
    extraReducers: {
        [getUserByUsernameAsync.fulfilled]: (state, action) => {
            console.log('get user by username successfully');
            // state.user = action.payload.tasks;
            return action.payload.tasks.result;
        },
    },
});

export const {} = userSlice.actions;

export const selectUser = (state) => state.user.user;

export default userSlice.reducer;
